UPDATE LocalizedText SET Text = REPLACE(Text, 'Grants the ability to construct a Spy', 'Can create two Spies') WHERE Language = 'en_US' AND Text LIKE '%Grants the ability to construct a Spy%'  ;
UPDATE LocalizedText SET Text = REPLACE(Text, 'Grants the ability to construct an additional Spy', 'Can create two more Spies') WHERE Language = 'en_US' AND Text LIKE '%Grants the ability to construct an additional Spy%'  ;
UPDATE LocalizedText SET Text = REPLACE(Text, '1 Spy and Spy capacity', '2 Spies and Spy capacity') WHERE Language = 'en_US' AND Text LIKE '%1 Spy and Spy capacity%'  ;